CREATE TABLE profile (id BIGINT AUTO_INCREMENT, sf_guard_user_id INT, first_name VARCHAR(255), last_name VARCHAR(255), email_address VARCHAR(255), gre_date DATE, favorite_word_id INT, city VARCHAR(255), state VARCHAR(2), country VARCHAR(255), INDEX sf_guard_user_id_idx (sf_guard_user_id), INDEX favorite_word_id_idx (favorite_word_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE word (id INT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, wordlist_id INT, INDEX wordlist_id_idx (wordlist_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE wordlist (id INT AUTO_INCREMENT, name VARCHAR(255) NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_group (id INT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_group_permission (group_id INT, permission_id INT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(group_id, permission_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_permission (id INT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_remember_key (id INT AUTO_INCREMENT, user_id INT, remember_key VARCHAR(32), ip_address VARCHAR(50), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id, ip_address)) ENGINE = INNODB;
CREATE TABLE sf_guard_user (id INT AUTO_INCREMENT, username VARCHAR(128) NOT NULL UNIQUE, algorithm VARCHAR(128) DEFAULT 'sha1' NOT NULL, salt VARCHAR(128), password VARCHAR(128), is_active TINYINT(1) DEFAULT '1', is_super_admin TINYINT(1) DEFAULT '0', last_login DATETIME, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX is_active_idx_idx (is_active), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_group (user_id INT, group_id INT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, group_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_permission (user_id INT, permission_id INT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, permission_id)) ENGINE = INNODB;
CREATE TABLE sf_wordnik_definition (id INT AUTO_INCREMENT, sf_wordnik_word_id INT, text TEXT, INDEX sf_wordnik_word_id_idx (sf_wordnik_word_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_wordnik_example (id INT AUTO_INCREMENT, sf_wordnik_word_id INT, text TEXT, INDEX sf_wordnik_word_id_idx (sf_wordnik_word_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_wordnik_related (id INT AUTO_INCREMENT, sf_wordnik_word_id INT, text TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX sf_wordnik_word_id_idx (sf_wordnik_word_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_wordnik_related_word (id INT AUTO_INCREMENT, sf_wordnik_word_id INT, text TEXT, INDEX sf_wordnik_word_id_idx (sf_wordnik_word_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_wordnik_word (id INT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, PRIMARY KEY(id)) ENGINE = INNODB;
ALTER TABLE profile ADD CONSTRAINT profile_sf_guard_user_id_sf_guard_user_id FOREIGN KEY (sf_guard_user_id) REFERENCES sf_guard_user(id);
ALTER TABLE profile ADD CONSTRAINT profile_favorite_word_id_sf_wordnik_word_id FOREIGN KEY (favorite_word_id) REFERENCES sf_wordnik_word(id);
ALTER TABLE word ADD CONSTRAINT word_wordlist_id_wordlist_id FOREIGN KEY (wordlist_id) REFERENCES wordlist(id);
